import "isomorphic-fetch";

export default class extends React.Component {
  static async getInitialProps() {
    let request = await fetch("https://api.audioboom.com/channels/recommended");
    let { body: channels } = await request.json();

    return { channels };
  }

  render() {
    const { channels } = this.props;

    return (
      <div>
        <header>Podcasts</header>

        <div className="channels">
          {channels.map(channel => (
            <div className="channel">
              <img src={channel.urls.logo_image.original} alt="logo" />
              <h2>{channel.title}</h2>
            </div>
          ))}
        </div>

        <style jsx>{`
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
            text-align: center;
          }
          .channels {
            display: grid;
            grid-gap: 15px;
          }
          .channel {
            display: block;
            padding-bottom: 0.5em;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
          }
          .channel img {
            width: 100%;
          }
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
        `}</style>
        <style jsx global>
          {`
            body {
              margin: 0;
              font-family: system-ui;
              background: white;
            }
          `}
        </style>
      </div>
    );
  }
}
