export default class extends React.Component {
  static async getInitialProps({ query }) {
    let idChannel = query.id;

    let [requestChannel, request, requestSeries] = await Promise.all([
      fetch(`https://api.audioboom.com/channels/${idChannel}`),
      fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`),
      fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`)
    ]);

    let dataChannel = await requestChannel.json();
    let channel = dataChannel.body.channel;

    let dataAudio = await request.json();
    let audioClips = dataAudio.body.audio_clips;

    let dataSeries = await requestSeries.json();
    let series = dataSeries.body.channels;

    return { channel, audioClips, series };
  }

  render() {
    const { channel, audioClips, series } = this.props;

    return (
      <div>
        <header>Podcast</header>
        <h1>{channel.title}</h1>

        <h2>Series</h2>
        {audioClips.map(clip => (
          <div> {clip.title} </div>
        ))}

        <h2>Ultimos podcasts</h2>
        {series.map(serie => (
          <div> {serie.title} </div>
        ))}

        <style jsx>{`
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
            text-align: center;
          }
          .channels {
            display: grid;
            grid-gap: 15px;
            padding: 15px;
            grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
          }
          .channel {
            display: block;
            padding-bottom: 0.5em;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
          }
          .channel img {
            width: 100%;
          }
          h1 {
            font-weight: 600;
            padding: 15px;
          }
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
        `}</style>
        <style jsx global>
          {`
            body {
              margin: 0;
              font-family: system-ui;
              background: white;
            }
          `}
        </style>
      </div>
    );
  }
}
