export default class extends React.Component {
  render() {
    return (
      <div>
        <img src="/static/platzi-logo.png" />
        <h1>HOLA MUNDO!</h1>
        <p>Bienvenidos todos al curso de Next.Js</p>
        <p>prueba</p>

        <style jsx>
          {`
            h1 {
              color: red;
            }
            div :global(p) {
              color: green;
            }
            img {
              max-width: 50%;
              display: block;
              margin: 0 auto;
            }
          `}
        </style>
        <style jsx global>
          {`
            body {
              background: blue;
            }
          `}
        </style>
      </div>
    );
  }
}
