export default class extends React.Component {
  render() {
    return (
      <div>
        <h1>HOLA MUNDO!</h1>
        <p>Bienvenidos todos al curso de Next.Js</p>

        <style jsx>
          {`
            h1 {
              color: red;
            }
            :global(p) {
              color: green;
            }
          `}
        </style>
        <style jsx global>
          {`
            body {
              background: blue;
            }
          `}
        </style>
      </div>
    );
  }
}
